/// @title The interface for a Uniswap V3 Pool
/// @notice A Uniswap pool facilitates swapping and automated market making between any two assets that strictly conform
/// to the ERC20 specification
/// @dev The pool interface is broken up into many smaller pieces
interface IUniswapV3Pool{
    function liquidity() external view returns (uint128);
    function token0() external view returns (address);
    function token1() external view returns (address);
}","keccak256":"0xd9fa5d92a3c7d1f53ff0ddbd4e04a5ef84faae96b067597e27016b8567e5fafc","license":"GPL-2.0-or-later"},"contracts/MockUniswapV3Factory.sol":{"content":"pragma solidity ^0.8.0;

import "./IUniswapV3Factory.sol";
import "./IUniswapV3Pool.sol";


contract MockUniswapV3Factory is IUniswapV3Factory {

    address _owner;
    mapping(address => mapping(address => mapping(uint24 => address))) public _pools;

    constructor() {
        _owner = msg.sender;
    }

	function getPool(
		address tokenA,
		address tokenB,
		uint24 fee
	) external view override returns (address) {
		return _pools[tokenA][tokenB][fee];
	}

	function setPool(
		address tokenA,
		address tokenB,
		uint24 fee,
		address pool
	) external {
		_pools[tokenA][tokenB][fee] = pool;
	}

	function owner() external view override returns (address) {
        return _owner;
    }

	function feeAmountTickSpacing(
		uint24 fee
	) external view override returns (int24) {
        return 0;
    }

	function createPool(
		address tokenA,
		address tokenB,
		uint24 fee
	) external override returns (address pool) {}

	function setOwner(address _owner) external override {}

	function enableFeeAmount(uint24 fee, int24 tickSpacing) external override {}
}","keccak256":"0x78e99b1a02d8fc8f9b98de5d6b797af671f0c87a1cc274648e8c6e8220dc1cde"}},"version":1}
